      <span class="token selector">.example.Text1 span, .example.Text1</span> <span class="token punctuation">{</span>
        <span class="token property">writing-mode</span><span class="token punctuation">:</span> horizontal-tb<span class="token punctuation">;</span>
        <span class="token property">-webkit-writing-mode</span><span class="token punctuation">:</span> horizontal-tb<span class="token punctuation">;</span>
        <span class="token property">-ms-writing-mode</span><span class="token punctuation">:</span> horizontal-tb<span class="token punctuation">;</span>
      <span class="token punctuation">}</span>

      <span class="token selector">.example.Text2 span, .example.Text2</span> <span class="token punctuation">{</span>
