        <span class="token property">-webkit-writing-mode</span><span class="token punctuation">:</span> vertical-lr<span class="token punctuation">;</span>
        <span class="token property">-ms-writing-mode</span><span class="token punctuation">:</span> vertical-lr<span class="token punctuation">;</span>
      <span class="token punctuation">}</span>

